import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Heading from '../Heading';
import GalleryData from './GalleryData';

const Portfolio = () => {

  const [item,setItem] = useState(GalleryData);

  const filterItems =((categItem)=>{
    const updatedItem =GalleryData.filter((curElem)=>{
      return curElem.category=== categItem;
    })
    setItem(updatedItem);
  })

  return (
    <>
     <section className='gallery-sec-wrp py-5'>
        <div className='container'>
          <div className='row'>
            <div className='col-12'>
              <Heading className='' title='my portfolio' />
            </div>
          </div>
          <div className='row'>
            <div className='col-12'>
              <div className='gallery-wrp'>
                <div className='gallery-menu'>
                  <ul className='clearfix reset-list'>
                    <li><Link onClick={()=>setItem('GalleryData')} href='#'>All</Link></li>
                    <li><Link onClick={()=>filterItems('webdesign')} href='#'>Web Design</Link></li>
                    <li><Link onClick={()=>filterItems('uiuxdesign')} href='#'>UI/UX Design</Link></li>
                    <li><Link onClick={()=>filterItems('reactjs')} href='#'>React Js</Link></li>
                  </ul>
                </div>
                <div className='gallery-grid'>
                  <ul className='clearfix reset-list'>
                    {GalleryData.map((items,index)=>(
                      <li key={index}>
                        <div className='gallery-grid-innr'>
                          <div className='gallery-img'>
                            <img class="img-fluid" src={items.image} alt='Gallery Images' />
                          </div>
                          <div className='gallery-grid-hover'>
                            <div className='gallery-grid-dsc'>
                              <h4>{items.name}</h4>
                              <button>{items.button}</button>
                            </div>
                          </div>
                        </div>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
     </section>
    </>
  )
}
export default Portfolio;
